import axios from 'axios'
import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { Form, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
// import Message from '../components/Message'
// import Loader from '../components/Loader'
import FormContainer from '../components/FormContainer'
import { createProduct } from '../actions/productActions'
// import css from '../components/css/Nav.css'

const ProductCreateScreen = ({ match, history }) => {
//   const productId = match.params.id

  const [name, setName] = useState('')
  const [price, setPrice] = useState(0)
  const [image, setImage] = useState('')
  const [brand, setBrand] = useState('')
  const [category, setCategory] = useState('')
  const [countInStock, setCountInStock] = useState(0)
  const [description, setDescription] = useState('')
  const [sellerName, setsellerName] = useState('')
  const [sellerEmail, setsellerEmail] = useState('')
  const [sellerContact, setsellerContact] = useState('')

  // const [uploading, setUploading] = useState(false)

  const dispatch = useDispatch()

//   const productDetails = useSelector((state) => state.productDetails)
//   const { loading, error, product } = productDetails

  const productCreate = useSelector((state) => state.productCreate)
  const {
    loading: loadingUpdate,
    error: errorUpdate,
    success: successUpdate,
  } = productCreate

//   useEffect(() => {
//     if (successUpdate) {
//       dispatch({ type: PRODUCT_UPDATE_RESET })
//       history.push('/admin/productlist')
//     } else {
//       if (!product.name || product._id !== productId) {
//         dispatch(listProductDetails(productId))
//       } else {
//         setName(product.name)
//         setPrice(product.price)
//         setImage(product.image)
//         setBrand(product.brand)
//         setCategory(product.category)
//         setCountInStock(product.countInStock)
//         setDescription(product.description)
//       }
//     }
//   }, [dispatch, history, productId, product, successUpdate])

  const uploadFileHandler = async (e) => {
    const file = e.target.files[0]
    const formData = new FormData()
    formData.append('image', file)
    // setUploading(true)

    try {
      const config = {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      }

      const { data } = await axios.post('/api/upload', formData, config)

      setImage(data)
      // setUploading(false)
    } catch (error) {
      console.error(error)
      // setUploading(false)
    }
  }

  const submitHandler = (e) => {
    e.preventDefault()
    dispatch(
      createProduct({
        productName: name,
        unitPrice: price,
        image: image,
        brand: brand,
        categoryId: category,
        description: description,
        unitsInStock: unitsInStock,
        sellerName: sellerName,
        sellerEmail: sellerEmail,
        sellerContact: sellerContact,
      })
    )
  }

  return (
    <div className="createscreen">
      <Link to='/admin/productlist' className='btn btn-light my-3 back-btn'>
        Go Back
      </Link>
      <FormContainer>
        <h1>Create Product</h1>
        {message && <Message variant="danger">{message}</Message>}
        {error && <Message variant="danger">{error}</Message>}
        {loading && <Loader />}
        <h4>Product Details</h4>

          <Form onSubmit={submitHandler}>
            <Form.Group controlId='name'>
              <Form.Label>Name</Form.Label>
              <Form.Control
                type='name'
                placeholder='Enter name'
                value={name}
                onChange={(e) => setName(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Form.Group controlId='price'>
              <Form.Label>Price</Form.Label>
              <Form.Control
                type='number'
                placeholder='Enter price'
                value={price}
                onChange={(e) => setPrice(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Form.Group controlId='image'>
              <Form.Label>Image</Form.Label>
              <Form.Control
                type='text'
                placeholder='Enter image url'
                value={image}
                onChange={(e) => setImage(e.target.value)}
              ></Form.Control>
              {/* <Form.File
                id='image-file'
                label='Choose File'
                custom
                onChange={uploadFileHandler}
              ></Form.File> */}
              {/* {uploading && <Loader />} */}
            </Form.Group>

            <Form.Group controlId='brand'>
              <Form.Label>Brand</Form.Label>
              <Form.Control
                type='text'
                placeholder='Enter brand'
                value={brand}
                onChange={(e) => setBrand(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Form.Group controlId='countInStock'>
              <Form.Label>Count In Stock</Form.Label>
              <Form.Control
                type='number'
                placeholder='Enter countInStock'
                value={countInStock}
                onChange={(e) => setCountInStock(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Form.Group controlId='category'>
              <Form.Label>Category</Form.Label>
              <Form.Control
                type='text'
                placeholder='Enter category'
                value={category}
                onChange={(e) => setCategory(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Form.Group controlId='description'>
              <Form.Label>Description</Form.Label>
              <Form.Control
                type='text'
                placeholder='Enter description'
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <h4>Selller Details</h4>
            <Form.Group controlId='sellerName'>
              <Form.Label>Seller Name</Form.Label>
              <Form.Control
                type='text'
                placeholder='Seller Name'
                value={sellerName}
                onChange={(e) => setsellerName(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId='sellerEmail'>
              <Form.Label>Seller Email</Form.Label>
              <Form.Control
                type='text'
                placeholder='Seller Email'
                value={sellerEmail}
                onChange={(e) => setsellerEmail(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId='sellerContact'>
              <Form.Label>Seller Contact</Form.Label>
              <Form.Control
                type='text'
                placeholder='Seller Contact'
                value={sellerContact}
                onChange={(e) => setsellerContact(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Button  variant="success" className="signin-btn" type='submit'>
               Create
            </Button>
          </Form>
      </FormContainer>
      <br/> 
      <br/>
    </div>
  )
}

export default ProductCreateScreen
